INSERT INTO `met_product` VALUES ('21', 'ls211可视成像搜索仪', '', '', '', '<p>\r\n	<b style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">ls211可视成像搜索仪</b><span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">&nbsp;是一款性能优越的</span><b style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">专业金属探测仪器，</b><span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">&nbsp;LS211金属探测器突破了目前市面上传统探测器的单一性，是将大范围搜索、定点定位、三维成像功能结合为一体的超性能金属探测器。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　ls211可视成像搜索仪是一款方便携带、容易操作的可视成像地下金属探测器，它的软件分析距离达到1000公尺，分析深度有25公尺。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　ls211可视成像搜索仪的探测模式是采用目前最先进的软件分析方法进行探测，它可以利用A模式大范围扫描找出目标区域异常情形的确切位置并具有分析不同结构的地下目标的功能，尤其是用B模式本仪器是全金属探测，具备强大的识别金属种类功能， 并可在显示屏上即时显示金属种类和形成金属三维模拟图像。当紧贴目标区域的地面时B模式精确定位找出目标后发出报警声主机根据目标信号软件进行分析，根据目标信号呈现出三维模拟图像，可以更精确找出目标。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　ls211可视成像搜索仪的探测速度是其他探测仪无法比拟的，它可用各种场所，不受场地限制，无论是崎岖不平的山地平原或草地树林均可使用。通过A模式大范围搜索、B模式定点定位三维成像更准确的找出埋藏于地下的金、银、铜、等贵金属。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">1、ls211可视成像搜索仪主要分为两个大的部分；A模式部分，B模式部分。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　A模式部分：用于大范围搜索 B模式部分：用于精确定点定位分析</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">2、部件名称及安装</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　A模式部分： 　　①A主机 ②充电口 ③电源开关 ④金属元素选择/静音按钮 ⑤显示屏 ⑥转动装置 ⑦底部紧固螺丝 ⑧探测棒信号指示灯 ⑨蜂鸣器</span></p>\r\n', '3', '7', '0', '0', '0', '0', '../upload/201304/1364784726.jpg', '../upload/201304/thumb/1364784726.jpg', '', '0', '1', '2013-04-01 10:42:02', '2013-04-01 10:42:02', 'admin', '0', '0', '', 'cn', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `met_product` VALUES ('22', 'AT601地下金属探测器', '', '', '', '<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">AT601列金属探测器是德国先进技术并采用进口元件制作的新产品。与同类产品比较，AT301仪器具有探测深度大、定位准确、分辨力强等特点，其最大使探测深度达到30米。金属探测器主要用来探测和识别隐埋在地下或其他介质内的金属。它广泛应用于：考古、探矿。发现埋藏在地下的金属文物和金银宝物。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　AT601金属探测器是专业探测地下金、银、铜、铅、锡、宝石贵重金属，具有探测深度深，探测范围大探测准确，易学易用等特点,便携式的设备。是一款远程大深度的定位器，探测范围1300米，最大极限探矿深度大约70米。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　AT601金属探测系统采用德国科学家高斯理论利用物体原子振荡频率来达到发现宝物贵金属的功能，由于地质结构的原因，旧时金属探测仪器在探测时会产生矿化反应。AT601型金属探测器采用了先进的地平衡调节系统，它只选择我们所需要的金属信号，消除其它一切杂物所引起的信号，这样就有效地排除了令人讨厌的&ldquo;矿化反应&rdquo;，大大提高了仪器的探测深度和准确性。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　技术参数</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　产品名称：AT601型金属探测仪</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　搜索系统：微处理器控制及反射传导性</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　搜索范围：1000米</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　探测深度：25米</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　能 源：12V 1800mAh（主机） 5V 800mAh（搜索仪）</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　发射频率：6-6.8KHz</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　信号频率：360-440Hz</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　重量：4.8磅</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　充电方法：本仪器内已经安装了可充电电池，注意区分主机和搜索仪的大小充电器，充电时间8小时。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　注意：大充电器主机专用，小充电器搜索仪专用，充电器不能混用否则会烧坏主机，如果机器长期不用应每月充电一次。 　　警告；必须确保所在地的标准电压和频率的额定电压和功率相匹配</span></p>\r\n', '3', '7', '0', '0', '0', '0', '../upload/201304/1364784964.png', '../upload/201304/thumb/1364784964.png', '', '0', '0', '2013-04-01 10:43:09', '2013-04-01 10:43:09', 'admin', '0', '0', '', 'cn', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `met_product` VALUES ('23', 'VR1000B 地下金属探测器', '', '', '', '<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">产品名称：VR1000B 超深度地下金属探测器</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　探测深度：20米</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　性　　能：世界最先进的地面探测仪之一，它采用微处理器控制，利用声波作为工作原理，采用多点定位的方法</span></p>\r\n<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">产品说明：</span></p>\r\n<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　VR1000B超级地下金属探测仪 现货超深度遥感金属探测仪是世界最先进的地面探测仪之一，它采用微处理器控制，利用声波作为工作原理，采用多点定位的方法。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　超深度遥感金属探测仪是世界最先进的地面探测仪之一，它采用微处理器控制，利用声波作为工作原理，采用多点定位的方法。准确测出金属物体所在的位置。能够在很大的范围内接受和发送物体的信号，最大探测范围可达到1英里。能够在很大范围内接收和发送物体信号，在几分钟内的扫描面积相当于普通金属探测器工作的几十倍，能够探测出金、银、铜、铅、锡、宝石六种金属矿。尤其适合有特殊需要的专业人士使用，是目前唯一能够搜索矿脉的金属探测系统。一块银元的探测深度可达到2米左右。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　电子探针检测地下金属（矿物）财宝的位置、大小、和深度。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　※结果显示：六路仪表和指示灯，</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　※一块银元现场测试深度达3米左右，搜索范围150米。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　※储藏量较大的金银矿探测深度向下可到30－50米.搜索范围1000米。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　■表头指示：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　金,银,铜,铝,锡,宝石,六个指示挡</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　仪表指针</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　■具体探测方法示例</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　1．确定目标为黄金,带定位功能</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　2．确定目标水平位置</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　3．确定目标深度。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　应用：搜索矿脉、野外探矿、考古研究、找寻宝藏</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　技术参数</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　最大限度持续功率：850 mA</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　频率范围：433 MHZ +/- 150 KHZ</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　内存储器：EEPROM 16k x 8 Bit</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　电压供给：9 - 12 V DC</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　存储温度：-40 C to +85 C</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　工作湿度：0-95% 无冷凝</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　重　　量：约4公斤</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　随产品有详细使用说明书也可来电来邮索要详细说明</span></p>\r\n', '3', '7', '0', '0', '0', '0', '../upload/201304/1364785027.jpg', '../upload/201304/thumb/1364785027.jpg', '', '0', '0', '2013-04-01 10:44:46', '2013-04-01 10:44:46', 'admin', '0', '0', '', 'cn', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `met_product` VALUES ('24', '探索者 地下金属探测器', '', '', '', '<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">产品名称：探索者 金银探测仪</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　探测深度：6-8米</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　性　　能：具有探测深度大、定位准确、分辨率强、操作简单方便的特点</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　产品说明：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　探索者地下金属探测采用美国最新电子技术，内设地平衡线路，它的地平衡模式，消除一切&ldquo;矿化反应&rdquo;给探测器带来的影响，而识别模式能区分被探测到的是有色金属还是黑色金属，大大提高了有效探测的深度及准确率，具有探测深度大、定位准确、分辨率强、操作简单方便的特点。为了最大限度的使用本产品的所有功能，在使用前请先仔细阅读本说明书。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　本产品除了军事上应用外，还适用于以下多个领域：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(1)公安刑事部门快度搜查</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(2)检测原料、燃料、食品中的 金属杂物</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(3)检查邮件、行包中的金属物品</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(4)检查地下的管道、电缆</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(5)考古、探矿</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(6)发现隐埋在地下的金银宝物和金属矿物废旧金属收购</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　产品功能特征：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(1)金属检测与金属识别的功能转换</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(2)地下金属及金属矿物探测</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(3)有色金属与 黑色 金属识别</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(4)自动调节功能</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(5)手动调节消除矿化反应</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(6)扬声器发声提示</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(7)电池低电提示</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(8)LED夜光功能</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(9)感应线圈断线自动报警（当连接电缆松脱时自动报警）</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　产品规格：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(1)最大探测深度：6-8米</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(2)探测方式：地平衡探测/识别探测</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(3)主振频率：4.19MHz</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(4)信号频率：7.2KHz</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(5)电　　源：DC9V(6 1.5VAA电池)</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(6)静态电流：40MA</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">　　(7)低　　电：7.2V+0.2V 7.2V-0.2V</span></p>\r\n', '3', '7', '0', '0', '0', '0', '../upload/201304/1364785112.jpg', '../upload/201304/thumb/1364785112.jpg', '', '0', '0', '2013-04-01 10:46:40', '2013-04-01 10:46:40', 'admin', '0', '0', '', 'cn', null, null, null, null, null, null, null, null, null, '0');
INSERT INTO `met_product` VALUES ('25', 'VR6000地下金属探测仪', '', '', '', '<p>\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">VR6000地下金属探测仪是超深度远程金属探测系统是世界先进的地面探测仪之一。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">VR6000地下金属探测仪特点：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">其特点是超深度大面积快速 描，能够在很大的范围内接受和发送物体的信号，并能有效防止普通金属探测器因人为原因而造成的疏漏。从而提高仪器的工作效率</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">VR6000地下金属探测仪使用简单，便于携带。</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">VR6000地下金属探测仪产品特性：</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<span style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\">采用微处理器控制，利用声波作为工作原理&nbsp;</span><br style=\"color: rgb(17, 34, 51); font-family: Verdana, Arial, Tahoma; line-height: 19px;\" />\r\n	<